mapper.select:
select
  user_id, dept_id, user_name, name, email, phonenumber, sex, avatar, password, status, del_flag, login_ip, login_date, create_by, create_time, update_by, update_time, remark, super_admin
from sys_user


        and user_id = #{userId}


        and dept_id = #{deptId}


        and user_name = #{userName}


        and name = #{name}


        and email = #{email}


        and phonenumber = #{phonenumber}


        and sex = #{sex}


        and avatar = #{avatar}


        and password = #{password}


        and status = #{status}


        and del_flag = #{delFlag}


        and login_ip = #{loginIp}


        and login_date = #{loginDate}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}


        and super_admin = #{superAdmin}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_user


        and user_id = #{userId}


        and dept_id = #{deptId}


        and user_name = #{userName}


        and name = #{name}


        and email = #{email}


        and phonenumber = #{phonenumber}


        and sex = #{sex}


        and avatar = #{avatar}


        and password = #{password}


        and status = #{status}


        and del_flag = #{delFlag}


        and login_ip = #{loginIp}


        and login_date = #{loginDate}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}


        and super_admin = #{superAdmin}
